query Iterate($clusterID:UInt64!, $namespace:String!, $limit:UInt64!,$from:String, $prefix:String) {
    globalOperation(clusterID:$clusterID, namespace:$namespace) {
        iterate(limit:$limit, from:$from, prefix:$prefix){
            address
            stateAvailable
            stateEntry {
                namespace
                familyName
                familyVersion
                payload
            }
        }
    }
}

