        retrieve(addresses:$addresses){
            stateAvailable
            address
            stateEntry {
                namespace
                familyName
                familyVersion
                payload
            }
        }
    }
}

query Search($clusterID:UInt64!, $namespace:String!, $input:SearchInput!) {
    globalOperation(clusterID:$clusterID, namespace:$namespace) {
        search(input:$input) {
            maxScore
            took
            total
            searchTime
            hits {
                id
                score
                index
                namespace
            }
            facets {
                field
                missing
                name
                other
                total
                terms {
                    count
                    term
                }
                numericRanges {
                    count
                    max
                    min
                    name
                }
                dateRanges {
                    count
                    end
                    name
                    start
                }
            }
        }
    }
}

